//==========================================
// ENCODERS DEFINITIOM
//==========================================

bool encoder_update_user(uint8_t index, bool clockwise) {
    if (index == 0) { /* First encoder */
        if(IS_LAYER_ON(_FUNC)) {
            if (clockwise) {
                tap_code(KC_MPRV);
            } else {
                tap_code(KC_MNXT);
            }
        } else { 
            if (clockwise) {
                tap_code(KC_VOLD);
            } else {
                tap_code(KC_VOLU);
            }
        }
    } else if (index == 1) { /* Second encoder */
        if(IS_LAYER_ON(_FUNC)) {
            if (clockwise) {
                tap_code(KC_BRID);
            } else {
                tap_code(KC_BRIU);
            }
        } else { 
            if (clockwise) {
                tap_code(KC_DOWN);
            } else {
                tap_code(KC_UP);
            }
        }
    }
    return true;
}

